INSERT INTO `note` VALUES ('2', '我的VS', '<p>\r\n	import java.lang.reflect.*;</p>\r\n<p>\r\n	@SuppressWarnings(&quot;unchecked&quot;)<br />\r\n	public class TestReflection03 {</p>\r\n<p>\r\n	&nbsp;public static void main(String[] args) {<br />\r\n	&nbsp;&nbsp;Class c;<br />\r\n	&nbsp;&nbsp;try {<br />\r\n	&nbsp;&nbsp;&nbsp;c = Class.forName(&quot;p1.Person&quot;);<br />\r\n	&nbsp;&nbsp;&nbsp;<br />\r\n	&nbsp;&nbsp;&nbsp;//调用有参数的构造方法<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Class[] argTypes = {String.class,int.class};<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Constructor constructor = c.getConstructor(argTypes);<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Object obj = constructor.newInstance(&quot;中国&quot;,5000);<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(obj);<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //调用无参构造方法1<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; constructor = c.getConstructor();&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //constructor = c.getConstructor(new Class[0]);<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //constructor = c.getConstructor(null);<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; obj = constructor.newInstance();<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(obj);<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //调用无参构造方法2<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; obj = c.newInstance();<br />\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; System.out.println(obj);<br />\r\n	&nbsp;&nbsp;} catch (Exception e) {<br />\r\n	&nbsp;&nbsp;&nbsp;e.printStackTrace();<br />\r\n	&nbsp;&nbsp;}</p>\r\n<p>\r\n	&nbsp;}</p>\r\n<p>\r\n	}</p>\r\n', 'p0', '2012-09-01 15:29:54', '1');
INSERT INTO `note` VALUES ('3', 'VC', '<html>\r\n	<head>\r\n		<title></title>\r\n	</head>\r\n	<body>\r\n		<p>\r\n			CKEditor config.js文件配置2011-10-11 13:14【config.js文件的配置】</p>\r\n		<p>\r\n			CKEDITOR.config.autoGrow_maxHeight</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.autoGrow_maxHeight = 400;</p>\r\n		<p>\r\n			默认值：0</p>\r\n		<p>\r\n			CKEDITOR.config.autoGrow_minHeight</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.autoGrow_minHeight = 300;</p>\r\n		<p>\r\n			默认值：200</p>\r\n		<p>\r\n			CKEDITOR.config.autoUpdateElement</p>\r\n		<p>\r\n			当提交包含有此编辑器的表单时，是否自动更新元素内的数据。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.autoUpdateElement = true;</p>\r\n		<p>\r\n			默认值：true</p>\r\n		<p>\r\n			CKEDITOR.config.baseFloatZIndex</p>\r\n		<p>\r\n			编辑器的z-index值。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.baseFloatZIndex = 2000;</p>\r\n		<p>\r\n			默认值：10000</p>\r\n		<p>\r\n			CKEDITOR.config.baseHref</p>\r\n		<p>\r\n			设置是使用绝对目录还是相对目录，空为相对目录。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.baseHref = &#39;http://www.example.com/path/&#39;;</p>\r\n		<p>\r\n			默认值：(空)</p>\r\n		<p>\r\n			CKEDITOR.config.blockedKeystrokes</p>\r\n		<p>\r\n			设置快捷键，可能与浏览器快捷键冲突。plugins/keystrokes/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.blockedKeystrokes = [</p>\r\n		<p>\r\n			CKEDITOR.CTRL + 66 /*B*/,</p>\r\n		<p>\r\n			CKEDITOR.CTRL + 73 /*I*/,</p>\r\n		<p>\r\n			CKEDITOR.CTRL + 85 /*U*/</p>\r\n		<p>\r\n			]</p>\r\n		<p>\r\n			默认值：如示例</p>\r\n		<p>\r\n			CKEDITOR.config.bodyClass</p>\r\n		<p>\r\n			设置编辑器body的class。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.bodyClass = &#39;contents&#39;;</p>\r\n		<p>\r\n			默认值：(空)</p>\r\n		<p>\r\n			CKEDITOR.config.bodyId</p>\r\n		<p>\r\n			设置编辑器body的ID。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.bodyId = &#39;contents_id&#39;;</p>\r\n		<p>\r\n			默认值：(空)</p>\r\n		<p>\r\n			CKEDITOR.config.BrowserContextMenuOnCtrl</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.BrowserContextMenuOnCtrl = false;</p>\r\n		<p>\r\n			默认值：true</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_backStyle</p>\r\n		<p>\r\n			设置编辑内元素的背景色的取值。plugins/colorbutton/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_backStyle = {</p>\r\n		<p>\r\n			element : &#39;span&#39;,</p>\r\n		<p>\r\n			styles : { &#39;background-color&#39; : &#39;#(color)&#39; }</p>\r\n		<p>\r\n			};</p>\r\n		<p>\r\n			默认值：如示例</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_colors</p>\r\n		<p>\r\n			设置前景色的取值。plugins/colorbutton/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_colors = &#39;00923E,F8C100,28166F&#39;;</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_colors = &#39;FontColor1/FF9900,FontColor2/0066CC,FontColor3/F00&#39;;</p>\r\n		<p>\r\n			默认值：</p>\r\n		<p>\r\n			&#39;000,800000,8B4513,2F4F4F,008080,000080,4B0082,696969,B22222,A52A2A,DAA520,006400,40E0D0,</p>\r\n		<p>\r\n			0000CD,800080,808080,F00,FF8C00,FFD700,008000,0FF,00F,EE82EE,A9A9A9,FFA07A,FFA500,FFFF00,</p>\r\n		<p>\r\n			00FF00,AFEEEE,ADD8E6,DDA0DD,D3D3D3,FFF0F5,FAEBD7,FFFFE0,F0FFF0,F0FFFF,F0F8FF,E6E6FA,FFF&#39;</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_enableMore</p>\r\n		<p>\r\n			是否在选择颜色时显示&ldquo;其它颜色&rdquo;选项。plugins/colorbutton/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_enableMore = false;</p>\r\n		<p>\r\n			默认值：false</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_foreStyle</p>\r\n		<p>\r\n			区块的前景色默认值设置。plugins/colorbutton/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.colorButton_foreStyle = {</p>\r\n		<p>\r\n			element : &#39;span&#39;,</p>\r\n		<p>\r\n			styles : { &#39;color&#39; : &#39;#(color)&#39; }</p>\r\n		<p>\r\n			};</p>\r\n		<p>\r\n			默认值：如示例</p>\r\n		<p>\r\n			CKEDITOR.config.contentsCss</p>\r\n		<p>\r\n			所需要添加的CSS文件，在此添加，可使用相对路径和网站的绝对路径。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.contentsCss = &#39;../contents.css&#39;;</p>\r\n		<p>\r\n			CKEDITOR.config.contentsCss = [&#39;/css/mysitestyles.css&#39;, &#39;/css/anotherfile.css&#39;];</p>\r\n		<p>\r\n			默认值：&#39;/contents.css&#39;</p>\r\n		<p>\r\n			&nbsp;CKEDITOR.config.contentsLangDirection</p>\r\n		<p>\r\n			文字方向。可设置：</p>\r\n		<p>\r\n			ui(根据系统设置)</p>\r\n		<p>\r\n			ltr(从左到右)</p>\r\n		<p>\r\n			rtl(从右到左)</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.contentsLangDirection = &#39;rtl&#39;;</p>\r\n		<p>\r\n			默认值：&#39;ui&#39;</p>\r\n		<p>\r\n			CKEDITOR.config.contentsLanguage</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.contentsLanguage = &#39;fr&#39;;</p>\r\n		<p>\r\n			默认值：根据系统设置</p>\r\n		<p>\r\n			CKEDITOR.config.corePlugins</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_bold</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			config.coreStyles_bold = { element : &#39;b&#39;, overrides : &#39;strong&#39; };</p>\r\n		<p>\r\n			config.coreStyles_bold = { element : &#39;span&#39;, attributes : {&#39;class&#39;: &#39;Bold&#39;} };</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_italic</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			config.coreStyles_italic = { element : &#39;i&#39;, overrides : &#39;em&#39; };</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_italic = { element : &#39;span&#39;, attributes : {&#39;class&#39;: &#39;Italic&#39;} };</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_strike</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_strike = { element : &#39;span&#39;, attributes : {&#39;class&#39;: &#39;StrikeThrough&#39;}, overrides : &#39;strike&#39; };</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_subscript</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_subscript = { element : &#39;span&#39;, attributes : {&#39;class&#39;: &#39;Subscript&#39;}, overrides : &#39;sub&#39; };</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_superscript</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_superscript = { element : &#39;span&#39;, attributes : {&#39;class&#39;: &#39;Superscript&#39;}, overrides : &#39;sup&#39; };</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_underline</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.coreStyles_underline = { element : &#39;span&#39;, attributes : {&#39;class&#39;: &#39;Underline&#39;}};</p>\r\n		<p>\r\n			CKEDITOR.config.customConfig</p>\r\n		<p>\r\n			CKeditor的配置文件，若不想配置，留空即可。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			// 需要加载配置文件。</p>\r\n		<p>\r\n			CKEDITOR.replace( &#39;myfiled&#39;, { customConfig : &#39;/myconfig.js&#39; } );</p>\r\n		<p>\r\n			// 不需要加载任何配置文件。</p>\r\n		<p>\r\n			CKEDITOR.replace( &#39;myfiled&#39;, { customConfig : &#39;&#39; } );</p>\r\n		<p>\r\n			默认值：/config.js&#39;</p>\r\n		<p>\r\n			CKEDITOR.config.defaultlanguage</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.defaultlanguage = &#39;it&#39;;</p>\r\n		<p>\r\n			默认值：&#39;en&#39;</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_backgroundCoverColor</p>\r\n		<p>\r\n			界面编辑框的背景色。plugins/dialog/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_backgroundCoverColor = &#39;rgb(255, 254, 253)&#39;;</p>\r\n		<p>\r\n			默认值：&#39;white&#39;</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_backgroundCoverOpacity</p>\r\n		<p>\r\n			遮罩背景的不透明度，数值应该在：0.0～1.0 之间。plugins/dialog/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_backgroundCoverOpacity = 0.7</p>\r\n		<p>\r\n			默认值：0.5</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_magnetDistance</p>\r\n		<p>\r\n			移动或者改变元素时，边框的吸附距离，单位：像素。plugins/dialog/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_magnetDistance = 30;</p>\r\n		<p>\r\n			默认值：20</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_startupFocusTab</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.dialog_startupFocusTab = true;</p>\r\n		<p>\r\n			默认值：false</p>\r\n		<p>\r\n			CKEDITOR.config.disableNativeSpellChecker</p>\r\n		<p>\r\n			是否拒绝本地拼写检查和提示，目前仅firefox和safari支持。plugins/wysiwygarea/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.disableNativeSpellChecker = false;</p>\r\n		<p>\r\n			默认值：ture</p>\r\n		<p>\r\n			CKEDITOR.config.disableNativeTableHandles</p>\r\n		<p>\r\n			进行表格编辑功能，如：添加行或列。目前仅firefox支持。plugins/wysiwygarea/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.disableNativeTableHandles = false;</p>\r\n		<p>\r\n			默认值：true</p>\r\n		<p>\r\n			CKEDITOR.config.disableObjectResizing</p>\r\n		<p>\r\n			是否开启图片和表格的改变大小的功能。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.disableObjectResizing = true;</p>\r\n		<p>\r\n			默认值：false</p>\r\n		<p>\r\n			CKEDITOR.config.docType</p>\r\n		<p>\r\n			设置HTML文档类型。</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.docType = &#39;&#39;;</p>\r\n		<p>\r\n			默认值：&#39;&#39;</p>\r\n		<p>\r\n			CKEDITOR.config.editingBlock</p>\r\n		<p>\r\n			是否对编辑区域进行渲染。plugins/editingblock/plugin.js</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.editingBlock = false;</p>\r\n		<p>\r\n			默认值：ture</p>\r\n		<p>\r\n			CKEDITOR.config.emailProtection</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			// href=&rdquo;mailto:tester@ckeditor.com?subject=subject&amp;body=body&rdquo;</p>\r\n		<p>\r\n			CKEDITOR.config.emailProtection = &#39;&#39;;</p>\r\n		<p>\r\n			// href=&rdquo;e-mail&rdquo;</p>\r\n		<p>\r\n			CKEDITOR.config.emailProtection = &#39;encode&#39;</p>\r\n		<p>\r\n			// href=&rdquo;JavaScript:mt(&#39;tester&#39;,&#39;ckeditor.com&#39;,&#39;subject&#39;,&#39;body&#39;)</p>\r\n		<p>\r\n			CKEDITOR.config.emailProtection = &#39;mt(NAME,DOMAIN,SUBJECT,BODY)&#39;</p>\r\n		<p>\r\n			默认值：空(等于disabled)</p>\r\n		<p>\r\n			CKEDITOR.config.enableTabKeyTools</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			config.enableTabKeyTools = false;</p>\r\n		<p>\r\n			默认值：ture</p>\r\n		<p>\r\n			CKEDITOR.config.enterMode</p>\r\n		<p>\r\n			编辑器中回车产生的标签，可设置：</p>\r\n		<p>\r\n			CKEDITOR.ENTER_P</p>\r\n		<p>\r\n			CKEDITOR.ENTER_BR</p>\r\n		<p>\r\n			CKEDITOR.ENTER_DIV</p>\r\n		<p>\r\n			示例：</p>\r\n		<p>\r\n			CKEDITOR.config.enterMode = CKEDITOR.ENTER_BR;</p>\r\n		<p>\r\n			CKEDITOR.c